--create database
CREATE DATABASE PROJECTDBI202
DROP DATABASE PROJECTDBI202
--create table guard
CREATE TABLE GUARD(
	GuardID INT PRIMARY KEY,
	[First Name] NVARCHAR(45) NOT NULL,
	[Last Name] NVARCHAR(45) NOT NULL,
	Email NVARCHAR(255) CHECK(Email LIKE '%@fpt.edu.vn')NOT NULL,
	Phone VARCHAR(11) UNIQUE CHECK(Phone LIKE '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]' OR Phone LIKE '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]')
)

--create table student
CREATE TABLE STUDENT(
	StudentID VARCHAR(7) PRIMARY KEY NOT NULL CHECK(StudentID LIKE 'SE[0-9][0-9][0-9][0-9][0-9]' OR StudentID LIKE 'SB[0-9][0-9][0-9][0-9][0-9]' OR StudentID LIKE 'HE[0-9][0-9][0-9][0-9][0-9]'),
	GuardID INT FOREIGN KEY REFERENCES dbo.GUARD(GuardID),
	[First Name] NVARCHAR(45) NOT NULL,
	[LastName] NVARCHAR(45) NOT NULL,
	Email NVARCHAR(255) CHECK(Email LIKE '%@fpt.edu.vn') NOT NULL,
	Phone CHAR(11) UNIQUE CHECK(Phone LIKE '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]' OR Phone LIKE '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]'),
	Gender CHAR(1) DEFAULT 'M' CHECK(Gender IN('F','M')) NOT NULL,
	Course CHAR(3) CHECK(Course LIKE 'K[0-9][0-9]') NOT NULL,
	[Role] NVARCHAR(30) DEFAULT N'Thành Viên' CHECK(Role IN (N'Thành Viên', N'Trưởng Phòng')) NOT NULL ,
	Country NVARCHAR(50) DEFAULT N'Việt Nam' NOT NULL
)

--create table room
CREATE TABLE ROOM(
	RoomID CHAR(5) PRIMARY KEY CHECK(RoomID LIKE '[A-F][1-5][0-9][0-9][0-9]' OR RoomID LIKE '[A-F][1-5][0-9][0-9][0-9][0-9]'),
	[Name Dom] VARCHAR(6) CHECK([Name Dom] IN ('A','B','C','D','F')),
	[Floor] SMALLINT CHECK([Floor] BETWEEN 1 AND 5) NOT NULL ,
	[Location] NVARCHAR(5) CHECK(Location IN (N'Trái', N'Phải')) NOT NULL,
	[Number Bed] SMALLINT CHECK([Number Bed] BETWEEN 1 AND 12) NOT NULL,
	Gender CHAR(1) DEFAULT 'M' CHECK(Gender IN('F','M')) NOT NULL,
	[Status] SMALLINT DEFAULT 1 CHECK(Status BETWEEN 0 AND 1) NOT NULL
)

--create table manager room
CREATE TABLE [MANAGER ROOM](
	RoomID CHAR(5) FOREIGN KEY REFERENCES dbo.ROOM(RoomID) CHECK(RoomID LIKE '[A-F][1-5][0-9][0-9][0-9]' OR RoomID LIKE '[A-F][1-5][0-9][0-9][0-9][0-9]'),
	GuardID INT FOREIGN KEY REFERENCES dbo.GUARD(GuardID) NOT NULL,
	[Name Dom] CHAR(1) CHECK([Name Dom] IN ('A','B','C','D','F')) NOT NULL,
	Price VARCHAR(10) CHECK(Price LIKE '%VND') NOT NULL
)

--create table register
CREATE TABLE REGISTER(
	RoomID CHAR(5) FOREIGN KEY REFERENCES dbo.ROOM(RoomID) CHECK(RoomID LIKE '[A-F][1-5][0-9][0-9][0-9]' OR RoomID LIKE '[A-F][1-5][0-9][0-9][0-9][0-9]'),
	StudentID VARCHAR(7) FOREIGN KEY REFERENCES dbo.STUDENT(StudentID) CHECK(StudentID LIKE 'SE[0-9][0-9][0-9][0-9][0-9]' OR StudentID LIKE 'SB[0-9][0-9][0-9][0-9][0-9]' OR StudentID LIKE 'HE[0-9][0-9][0-9][0-9][0-9]') NOT NULL,
	[Check-in] DATETIME CHECK([Check-in] <= GETDATE()) NOT NULL,
	[Check Price] SMALLINT DEFAULT 1 CHECK([Check Price] BETWEEN 0 AND 1) NOT NULL
)

--create table items
CREATE TABLE ITEMS(
	ItemID INT PRIMARY KEY NOT NULL,
	[Name] NVARCHAR(50) NOT NULL,
	Price INT NOT NULL,
	Content NVARCHAR(255) NOT NULL
)

--create table fixdetail
CREATE TABLE [FIX DETAIL](
	RoomID CHAR(5) CHECK(RoomID LIKE '[A-F][1-5][0-9][0-9][0-9]' OR RoomID LIKE '[A-F][1-5][0-9][0-9][0-9][0-9]') FOREIGN KEY REFERENCES dbo.ROOM(RoomID),
	ItemID INT FOREIGN KEY REFERENCES dbo.ITEMS(ItemID),
	Fixer NVARCHAR(90),
	[Date Report] DATETIME CHECK([Date Report] <= GETDATE()) NOT NULL,
	[Date Fix] DATETIME CHECK([Date Fix]<= GETDATE()),
	Quantity INT NOT NULL
)

--create table penalize
CREATE TABLE PENALIZE(
	PenalizeID INT PRIMARY KEY,
	[Name] NVARCHAR(255) NOT NULL,
	Price INT NOT NULL
)

--create table student penalize
CREATE TABLE [STUDENT PENALIZE](
	StudentID VARCHAR(7) CHECK(StudentID LIKE 'SE[0-9][0-9][0-9][0-9][0-9]' OR StudentID LIKE 'SB[0-9][0-9][0-9][0-9][0-9]' OR StudentID LIKE 'HE[0-9][0-9][0-9][0-9][0-9]') FOREIGN KEY REFERENCES dbo.STUDENT(StudentID),
	PenalizeID INT FOREIGN KEY REFERENCES dbo.PENALIZE(PenalizeID),
	[Date] DATETIME NOT NULL CHECK(Date<=GETDATE()),
	Quantity SMALLINT DEFAULT 1 NOT NULL
)


